</th>                        <th width="90">Status</th>                                                    <th width="115">Action</th>                    </tr>                </thead>                <tbody>                    <?php foreach ($posts as $post) { ?>                        <tr>                            <td><?php echo $post->id ?></td>                                                   <td><img src="<?php echo getPhoto($post->thumb, $post->post_title, 100, 80); ?>" class="img-responsive"></td>                                                                     <td class="post_details">                                        <h4><?php echo getShortContent($post->post_title, 40); ?></h4>                                                                    <p><?php echo getShortContent($post->content, 40); ?></p>                                                                   </td>                            <td><?php echo caretoryParentIdByName($post->parent_id); ?></td>                            <td><?php echo globalDateFormat( $post->created ) ?></td>                            <td><?php echo statusGroup($post->status,$post->id); ?></td>                            <td>                            <?php                                echo anchor(site_url($post->post_url), '<i class="fa fa-fw fa-external-link"></i>', 'class="btn btn-xs btn-default" target="_blank" title="Preveiw"');                                echo anchor(site_url( Backend_URL . 'cms/update_post/' . $post->id), '<i class="fa fa-fw fa-edit"></i>', 'class="btn btn-xs btn-default"  title="Edit"');                                echo anchor(site_url( Backend_URL . "cms/delete/{$post->id}"), '<i class="fa fa-fw fa-trash"></i>', 'class="btn btn-xs btn-danger"  title="Move to Trash" onclick="javasciprt: return confirm(\'Are You Sure ?\')"');                            ?>                            </td>                        </tr>                    <?php } ?>                </tbody>            </table>                        <div class="row">                <div class="col-md-6">                    <span class="btn btn-primary">Total Page: <?php echo $total_rows ?></span>	                    </div>                <div class="col-md-6 text-right">                    <?php echo $pagination ?>                </div>              </div>        </div>    </div></section>